import { useContext } from "react";
import { Link } from "react-router-dom";
import { StoreContext } from "../store";
// import { pageContentsSet, activeNavItemSet } from "../actions";
// import { getJSON } from "../api";

export default function ShopNavItem(props) {
  const { children, to, className, activeClassName } = props;
  const { state, dispatch } = useContext(StoreContext);

  const onClick = () => {
    pageContentsSet(dispatch, children, "/");
    activeNavItemSet(dispatch, to);
  };
  
  return (
    <Link to={to}>
      <div
        onClick={onClick}
        className={`
            ${className} 
            ${state.shopnavBar.activeItem === to ? activeClassName : ""}`}
      >
        {children}
      </div>
    </Link>
  );
}
